      protected void autonumber() {
        try {
            String sql = "SELECT Id_bayar FROM pembayaran ORDER BY Id_bayar DESC LIMIT 1";
            Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            txtidbyr.setText("B0001");
            while (hasil.next()) {
                System.out.println(hasil.getString("Id_bayar").substring(2));
                String id_byr = hasil.getString("Id_bayar").substring(2);
                int AN = Integer.parseInt(id_byr) + 1;
                String Nol = "";

                if (AN < 10) {
                    Nol = "000";
                } else if (AN < 100) {
                    Nol = "00";
                } else if (AN < 1000) {
                    Nol = "0";
                } else if (AN < 10000) {
                    Nol = "";
                }

                txtidbyr.setText("B" + Nol + AN);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Auto number gagal " + e);
        }
    }
      
      //ITEM TERPLIH
      public void pembayaran() {
        ListSiswaBayar lcs = new ListSiswaBayar();
        lcs.ls = this;
        txtidpdb.setText(idpdb);
        txtnama.setText(namapdb);
    }
      
      //DATA TABEL
    protected void datatable(){
        Object[] Baris = {"Id_bayar","Id_pdb","Nama","Tgl_transaksi","Uang_gedung","Uang_spp","Uang_seragam","Uang_buku","Jumlah","Keterangan"};
        tabMode = new DefaultTableModel(null, Baris);
        String cariitem = txtcari.getText();
        
        try{
            String sql = "SELECT * FROM pembayaran WHERE Id_bayar like '%"+cariitem+"%' or nama like '%"+cariitem+"%' order by Id_bayar asc";
            Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while(hasil.next()){
                tabMode.addRow(new Object[]{
                    hasil.getString(1),
                    hasil.getString(2),
                    hasil.getString(3),
                    hasil.getString(4),
                    hasil.getString(5),
                    hasil.getString(6),
                    hasil.getString(7),
                    hasil.getString(8),
                    hasil.getString(9),
                    hasil.getString(10)
                });
            } tblpembayaran.setModel(tabMode);
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, "Data gagal di panggil"+e);
        }
    }

