    private void btnhitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnhitungActionPerformed
        // TODO add your handling code here:
        int gdng = Integer.parseInt(txtgedung.getText());
        int spp = Integer.parseInt(txtspp.getText());
        int seragam = Integer.parseInt(txtseragam.getText());
        int buku = Integer.parseInt(txtbuku.getText());
        
        int jumlah = (gdng+spp+seragam+buku);
        txtjml.setText(""+jumlah);
        
        if(jumlah >= 5000000){
            txtket.setText("LUNAS");
        }else{
            txtket.setText("BELUM LUNAS");
        }
    }//GEN-LAST:event_btnhitungActionPerformed

    private void tblpembayaranMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblpembayaranMouseClicked
        // TODO add your handling code here:
        DefaultTableModel tabmode = (DefaultTableModel)tblpembayaran.getModel();
        int bar = tblpembayaran.getSelectedRow();
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
        String d = tabmode.getValueAt(bar, 3).toString();
        String e = tabmode.getValueAt(bar, 4).toString();
        String f = tabmode.getValueAt(bar, 5).toString();
        String g = tabmode.getValueAt(bar, 6).toString();
        String h = tabmode.getValueAt(bar, 7).toString();
        String i = tabmode.getValueAt(bar, 8).toString();
        String j = tabmode.getValueAt(bar, 9).toString();
        
        txtidbyr.setText(a);
        txtidpdb.setText(b);
        txtnama.setText(c);
        
        java.util.Date tgltra = null;
        try {
            tgltra = new SimpleDateFormat("yyyy-MM-dd").parse(d);
        } catch (ParseException ex) {
            Logger.getLogger(Pembayaran.class.getName()).log(Level.SEVERE, null, ex);
        }
        tgltransaksi.setDate(tgltra);
        
        txtgedung.setText(e);
        txtspp.setText(f);
        txtseragam.setText(g);
        txtbuku.setText(h);
        txtjml.setText(i);
        txtket.setText(j);
    }//GEN-LAST:event_tblpembayaranMouseClicked

    private void btnubahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnubahActionPerformed
        // TODO add your handling code here:
        String sql = "UPDATE pembayaran SET Id_bayar=?,Id_pdb=?,Nama=?,Tgl_transaksi=?,Uang_gedung=?,Uang_spp=?,Uang_seragam=?,Uang_buku=?,Jumlah=?,KetBayar=? WHERE Id_bayar='"+txtidbyr.getText()+"'";
        try {
            String thlhr = "yyyy-MM-dd";
            SimpleDateFormat pj = new SimpleDateFormat(thlhr);
            String th = String.valueOf(pj.format(tgltransaksi.getDate()));
            PreparedStatement stat = conn.prepareStatement(sql);
           
            stat.setString(1, txtidbyr.getText());
            stat.setString(2, txtidpdb.getText());
            stat.setString(3, txtnama.getText());
            stat.setString(4, th);
            stat.setString(5, txtgedung.getText());
            stat.setString(6, txtspp.getText());
            stat.setString(7, txtseragam.getText());
            stat.setString(8, txtbuku.getText());
            stat.setString(9, txtjml.getText());
            stat.setString(10, txtket.getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data berhasil di ubah");
            kosong();
            txtidbyr.requestFocus();
        } catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal di ubah"+e);
        }
        datatable();
    }//GEN-LAST:event_btnubahActionPerformed

